import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { BookstoreServiceService } from 'src/app/services/bookstore-service.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  books: any[] = [];

  constructor(private bookService: BookstoreServiceService ,private router:Router){

    this.fetchBooks();
  }


  fetchBooks(){
    this.bookService.getBooks().subscribe((data) =>{

      this.books = data;
      console.log("Books data",data);
     });
  }
  updateBook(book: any) {
    this.router.navigate(['/edit', book.id]);
  }
  deleteBook(id: number) {
    const confirmDelete = window.confirm('Are you sure you want to delete this book?');

    if (confirmDelete) {
      this.bookService.deleteBook(id).subscribe(() => {
        this.fetchBooks();
      });
    }
  }
  addNewBook() {
    this.router.navigate(['/edit']);
  }
}
<div class="container mt-5">
  <h2>Book List</h2>
  <button class="btn btn-success mb-3" (click)="addNewBook()">Add New Book</button>
  <table class="table">
    <thead>
      <tr>
        <th>Title</th>
        <th>Author</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let book of books">
        <td>{{ book.title }}</td>
        <td>{{ book.author }}</td>
        <td>
          <button class="btn btn-info" (click)="updateBook(book)">Update</button>
          <button class="btn btn-danger" (click)="deleteBook(book.id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
