import { Component, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { BookstoreServiceService } from 'src/app/services/bookstore-service.service';

@Component({
  selector: 'app-edit-books',
  templateUrl: './edit-books.component.html',
  styleUrls: ['./edit-books.component.css']
})
export class EditBooksComponent {
  @Input() selectedBook: any; // Input property to receive selected book for update

  bookForm: FormGroup;

  constructor(private fb: FormBuilder,private bookservice:BookstoreServiceService,private router: Router) {
    this.bookForm = this.fb.group({
      title: ['', Validators.required],
      author: ['', Validators.required],
    });
  }
  ngOnChanges() {
    // Update form values when a book is selected for update
    if (this.selectedBook) {
      this.bookForm.patchValue(this.selectedBook);
    }
  }
  onSubmit() {
    if (this.bookForm.valid) {
      if (this.selectedBook) {
        // Update existing book
        this.bookservice.updateBook(this.selectedBook.id, this.bookForm.value).subscribe(() => {
          this.router.navigate(['/home']);
        });
      } else {
        // Add new book
        this.bookservice.addBook(this.bookForm.value).subscribe(() => {
          this.router.navigate(['/home']);
        });
      }
    }
  }


}
